import cv2, numpy

def hextobin(num):
    return "".join(['{:04b}'.format(int(x,16)) for x in num])

def img_size(img):
    l, b = len(img), len(img[0])

    temp = numpy.ndarray((l*2, b*2, 3))

    for i in range(l):
        for j in range(b):
            x = i*2
            y = j*2
            temp[x][y] = temp[x + 1][y] = temp[x][y + 1] = temp[x + 1][y + 1] = img[i][j]
    return temp

